buildNoteFromProps(nodes) {
    // initialize root
    out = []
    root, childrenIds = getRoot nodes {
        if root not in nodes
            throw DendronError // this gets caught in the engine
        return root
    }
    out.push root
    parentNodes = [root]
    nodeIds = childrenIds

    while nodeIds {
      nodeIds.map {
        nodeProps = nodes.find it.id
        @toNote nodeProps, parentNodes
      }
    }
}

